import React from 'react';
import { Streamlit, StreamlitComponentBase, withStreamlitConnection } from "streamlit-component-lib";

interface State {
  selectedImageTitle: string | null;
  currentIndex: number;
}

interface CardProps {
  image: string;
  title: string;
  description: string;
}

class CarouselComponent extends StreamlitComponentBase<State> {
  public state: State = { selectedImageTitle: null, currentIndex: 0 };

  private cards: CardProps[] = [
    // ... your card data here ...
  ];

  public render = (): React.ReactNode => {
    const { theme } = this.props;
    const style: React.CSSProperties = {
      fontFamily: theme?.font,
      color: theme?.textColor,
    };

    return (
      <div style={style} className="carousel-container">
        <button className="carousel-arrow carousel-arrow-left" onClick={this.handlePrev}>&lt;</button>
        <div className="carousel-content">
          {this.cards.slice(this.state.currentIndex, this.state.currentIndex + 3).map((card, index) => (
            <div key={index} className="carousel-card" onClick={() => this.handleCardClick(card.title)}>
              <img src={card.image} alt={card.title} className="carousel-image" />
              <div className="carousel-card-content">
                <h3>{card.title}</h3>
                <p>{card.description}</p>
              </div>
            </div>
          ))}
        </div>
        <button className="carousel-arrow carousel-arrow-right" onClick={this.handleNext}>&gt;</button>
      </div>
    );
  }

  private handleCardClick = (title: string): void => {
    this.setState({ selectedImageTitle: title }, () => {
      Streamlit.setComponentValue(this.state.selectedImageTitle);
    });
  }

  private handleNext = (): void => {
    this.setState(prevState => ({
      currentIndex: (prevState.currentIndex + 1) % (this.cards.length - 2)
    }));
  }

  private handlePrev = (): void => {
    this.setState(prevState => ({
      currentIndex: (prevState.currentIndex - 1 + (this.cards.length - 2)) % (this.cards.length - 2)
    }));
  }
}

export default withStreamlitConnection(CarouselComponent);
