import {
  Streamlit,
  StreamlitComponentBase,
  withStreamlitConnection,
} from "streamlit-component-lib"
import React, { ReactNode } from "react"

interface State {
  selectedImageTitle: string | null
  currentIndex: number
}

interface CardProps {
  image: string
  title: string
  description: string
}

class CarouselComponent extends StreamlitComponentBase<State> {
  public state: State = { selectedImageTitle: null, currentIndex: 0 }

  private cards: CardProps[] = [
    { image: 'https://source.unsplash.com/random/1', title: 'Card 1', description: 'Info which directs to the other page.' },
    { image: 'https://source.unsplash.com/random/2', title: 'Card 2', description: 'Info which directs to the other page.' },
    { image: 'https://source.unsplash.com/random/3', title: 'Card 3', description: 'Info which directs to the other page.' },
    { image: 'https://source.unsplash.com/random/4', title: 'Card 4', description: 'Info which directs to the other page.' },
    { image: 'https://source.unsplash.com/random/5', title: 'Card 5', description: 'Info which directs to the other page.' },
  ]

  public render = (): ReactNode => {
    const { theme } = this.props
    const style: React.CSSProperties = {
      width: '100%',
      fontFamily: theme?.font,
      color: theme?.textColor,
    }

    return (
      <div style={style}>
        <div className="relative">
          <div className="flex space-x-4">
            {this.cards.slice(this.state.currentIndex, this.state.currentIndex + 3).map((card, index) => (
              <div
                className="flex-none w-1/3 p-4 bg-white rounded-lg shadow-md cursor-pointer"
                key={index}
                onClick={() => this.handleCardClick(card.title)}
              >
                <img className="w-full h-32 object-cover mb-4 rounded-lg" src={card.image} alt={card.title} />
                <h3 className="text-lg font-bold">{card.title}</h3>
                <p className="text-gray-500">{card.description}</p>
              </div>
            ))}
          </div>
          <div className="flex items-center justify-between mt-4">
            <button className="w-6 h-6 rounded-full bg-gray-300" onClick={this.handlePrev}>&lt;</button>
            <button className="w-6 h-6 rounded-full bg-gray-300" onClick={this.handleNext}>&gt;</button>
          </div>
        </div>
        {this.state.selectedImageTitle && (
          <div className="mt-4">
            <p>Selected Image: {this.state.selectedImageTitle}</p>
          </div>
        )}
      </div>
    )
  }

  private handleCardClick = (title: string): void => {
    this.setState({ selectedImageTitle: title }, () => {
      Streamlit.setComponentValue(this.state.selectedImageTitle)
    })
  }

  private handleNext = (): void => {
    this.setState(prevState => ({
      currentIndex: (prevState.currentIndex + 1) % this.cards.length
    }))
  }

  private handlePrev = (): void => {
    this.setState(prevState => ({
      currentIndex: (prevState.currentIndex - 1 + this.cards.length) % this.cards.length
    }))
  }
}

export default withStreamlitConnection(CarouselComponent)
