import React, { useMemo } from 'react';
import { Streamlit, StreamlitComponentBase, withStreamlitConnection } from "streamlit-component-lib";

interface State {
  selectedTitle: string | null;
  selectedLink: string | undefined;  // Changed from string | null
  selectedDescription: string | undefined;  // Changed from string | null
  currentIndex: number;
}

interface CardProps {
  image: string;
  title: string;
  link?: string;
  description?: string;
}

interface CarouselCardProps {
  card: CardProps;
  layout: string;
  onClick: (title: string, link: string | undefined, description: string | undefined, layout: string) => void;
  styles: {
    cardStyle: React.CSSProperties;
    imageStyle: React.CSSProperties;
    contentStyle: React.CSSProperties;
    titleStyle: React.CSSProperties;
    descriptionStyle: React.CSSProperties;
  };
}

const CarouselCard: React.FC<CarouselCardProps> = React.memo(({ card, layout, onClick, styles }) => {
  return (
    <div className="carousel-card" style={styles.cardStyle} onClick={() => onClick(card.title, card.link, card.description, layout)}>
      <img src={card.image} alt={card.title} style={styles.imageStyle} />
      <div style={styles.contentStyle}>
        <h3 style={styles.titleStyle}>{card.title}</h3>
        {layout === 'alternate' && card.description && (
          <p style={styles.descriptionStyle}>{card.description}</p>
        )}
      </div>
    </div>
  );
});

class CarouselComponent extends StreamlitComponentBase<State> {
  public state: State = { selectedTitle: null, selectedLink: undefined, selectedDescription: undefined, currentIndex: 0 };

  public render = (): React.ReactNode => {
    const { theme } = this.props;
    const cards: CardProps[] = this.props.args["cards"] || [];
    const layout = this.props.args["layout"] || 'default';

    const styles = {
      cardStyle: {
        border: '1px solid #ddd',
        borderRadius: '0',
        overflow: 'hidden',
        display: 'flex',
        flexDirection: layout === 'alternate' ? 'row' : 'column',
        height: layout === 'alternate' ? '150px' : 'auto',
      } as React.CSSProperties,
      imageStyle: {
        width: layout === 'alternate' ? '30%' : '100%',
        height: layout === 'alternate' ? '100%' : 'auto',
        objectFit: 'cover',
      } as React.CSSProperties,
      contentStyle: {
        padding: '10px',
        flex: 1,
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'center',
        alignItems: layout === 'default' ? 'center' : 'flex-start',
      } as React.CSSProperties,
      titleStyle: {
        margin: '0 0 5px 0',
        fontSize: '16px',
        fontWeight: 'bold',
        textAlign: layout === 'default' ? 'center' : 'left',
      } as React.CSSProperties,
      descriptionStyle: {
        margin: '0',
        fontSize: '14px',
        color: '#666',
      } as React.CSSProperties,
    };

    const visibleCards = useMemo(() =>
        cards.slice(this.state.currentIndex, this.state.currentIndex + 3),
      [cards, this.state.currentIndex]
    );

    return (
      <div className="carousel-container">
        <button className="carousel-arrow carousel-arrow-left" onClick={this.handlePrev}>&lt;</button>
        <div className="carousel-content">
          {visibleCards.map((card, index) => (
            <CarouselCard
              key={index}
              card={card}
              layout={layout}
              onClick={this.handleCardClick}
              styles={styles}
            />
          ))}
        </div>
        <button className="carousel-arrow carousel-arrow-right" onClick={this.handleNext}>&gt;</button>
      </div>
    );
  }

  private handleCardClick = (title: string, link: string | undefined, description: string | undefined, layout: string): void => {
    this.setState({ selectedTitle: title, selectedLink: link, selectedDescription: description }, () => {
      if (layout === 'alternate') {
        Streamlit.setComponentValue({ title: title, description: description });
      } else {
        Streamlit.setComponentValue({ title: title, link: link });
      }
    });
  }

  private handleNext = (): void => {
    const cards: CardProps[] = this.props.args["cards"] || [];
    this.setState(prevState => ({
      currentIndex: (prevState.currentIndex + 1) % (cards.length - 2)
    }));
  }

  private handlePrev = (): void => {
    const cards: CardProps[] = this.props.args["cards"] || [];
    this.setState(prevState => ({
      currentIndex: (prevState.currentIndex - 1 + (cards.length - 2)) % (cards.length - 2)
    }));
  }
}

export default withStreamlitConnection(CarouselComponent);
