import React from "react"
import ReactDOM from "react-dom"
import { DefaultCarousel, AlternateCarousel } from "./CarouselComponent"
import { ComponentProps, Streamlit } from "streamlit-component-lib"

// Create a function to render the appropriate carousel based on the layout prop
const CarouselWrapper: React.FC<ComponentProps> = (props: ComponentProps) => {
  const layout = props.args["layout"] || 'default';

  React.useEffect(() => {
    // Notify Streamlit that the component is ready
    Streamlit.setComponentReady();
  }, []);

  if (layout === 'alternate') {
    return <AlternateCarousel {...props} />;
  } else {
    return <DefaultCarousel {...props} />;
  }
}

// Render the component
ReactDOM.render(
  <React.StrictMode>
    <CarouselWrapper />
  </React.StrictMode>,
  document.getElementById("root")
)
