import React, { useState, useMemo, useCallback } from 'react';
import { ComponentProps, Streamlit, withStreamlitConnection } from "streamlit-component-lib";

interface CardProps {
  image: string;
  title: string;
  link?: string;
  description?: string;
}

interface CarouselCardProps {
  card: CardProps;
  layout: string;
  onClick: (title: string, link: string | undefined, description: string | undefined, layout: string) => void;
  styles: {
    cardStyle: React.CSSProperties;
    imageStyle: React.CSSProperties;
    contentStyle: React.CSSProperties;
    titleStyle: React.CSSProperties;
    descriptionStyle: React.CSSProperties;
  };
}

const CarouselCard: React.FC<CarouselCardProps> = React.memo(({ card, layout, onClick, styles }) => {
  return (
    <div className="carousel-card" style={styles.cardStyle} onClick={() => onClick(card.title, card.link, card.description, layout)}>
      <img src={card.image} alt={card.title} style={styles.imageStyle} />
      <div style={styles.contentStyle}>
        <h3 style={styles.titleStyle}>{card.title}</h3>
        {layout === 'alternate' && card.description && (
          <p style={styles.descriptionStyle}>{card.description}</p>
        )}
      </div>
    </div>
  );
});

const CarouselComponent: React.FC<ComponentProps> = (props: ComponentProps) => {
  const [currentIndex, setCurrentIndex] = useState(0);

  const cards: CardProps[] = props.args["cards"] || [];
  const layout = props.args["layout"] || 'default';

  const styles = {
    cardStyle: {
      border: '1px solid #ddd',
      borderRadius: '0',
      overflow: 'hidden',
      display: 'flex',
      flexDirection: layout === 'alternate' ? 'row' : 'column',
      height: layout === 'alternate' ? '150px' : 'auto',
    } as React.CSSProperties,
    imageStyle: {
      width: layout === 'alternate' ? '30%' : '100%',
      height: layout === 'alternate' ? '100%' : 'auto',
      objectFit: 'cover',
    } as React.CSSProperties,
    contentStyle: {
      padding: '10px',
      flex: 1,
      display: 'flex',
      flexDirection: 'column',
      justifyContent: 'center',
      alignItems: layout === 'default' ? 'center' : 'flex-start',
    } as React.CSSProperties,
    titleStyle: {
      margin: '0 0 5px 0',
      fontSize: '16px',
      fontWeight: 'bold',
      textAlign: layout === 'default' ? 'center' : 'left',
    } as React.CSSProperties,
    descriptionStyle: {
      margin: '0',
      fontSize: '14px',
      color: '#666',
    } as React.CSSProperties,
  };

  const handleCardClick = useCallback((title: string, link: string | undefined, description: string | undefined, layout: string) => {
    if (layout === 'alternate') {
      Streamlit.setComponentValue({ title: title, description: description });
    } else {
      Streamlit.setComponentValue({ title: title, link: link });
    }
  }, []);

  const handleNext = useCallback(() => {
    setCurrentIndex(prevIndex => (prevIndex + 1) % (cards.length - 2));
  }, [cards.length]);

  const handlePrev = useCallback(() => {
    setCurrentIndex(prevIndex => (prevIndex - 1 + (cards.length - 2)) % (cards.length - 2));
  }, [cards.length]);

  const visibleCards = useMemo(() =>
      cards.slice(currentIndex, currentIndex + 3),
    [cards, currentIndex]
  );

  return (
    <div className="carousel-container">
      <button className="carousel-arrow carousel-arrow-left" onClick={handlePrev}>&lt;</button>
      <div className="carousel-content">
        {visibleCards.map((card, index) => (
          <CarouselCard
            key={index}
            card={card}
            layout={layout}
            onClick={handleCardClick}
            styles={styles}
          />
        ))}
      </div>
      <button className="carousel-arrow carousel-arrow-right" onClick={handleNext}>&gt;</button>
    </div>
  );
};

export default withStreamlitConnection(CarouselComponent);
