import React from 'react';
import { Streamlit, StreamlitComponentBase, withStreamlitConnection } from "streamlit-component-lib";

interface State {
  selectedTitle: string | null;
  selectedLink: string | null;
  selectedDescription: string | null;
  currentIndex: number;
}

interface CardProps {
  image: string;
  title: string;
  link?: string;
  description?: string;
}

class BaseCarouselComponent extends StreamlitComponentBase<State> {
  public state: State = { selectedTitle: null, selectedLink: null, selectedDescription: null, currentIndex: 0 };

  protected renderCard(card: CardProps, index: number, cardStyle: React.CSSProperties, imageStyle: React.CSSProperties, contentStyle: React.CSSProperties, titleStyle: React.CSSProperties, descriptionStyle: React.CSSProperties): React.ReactNode {
    throw new Error("renderCard method must be implemented in derived class");
  }

  protected handleCardClick(card: CardProps): void {
    throw new Error("handleCardClick method must be implemented in derived class");
  }

  public render = (): React.ReactNode => {
    const cards: CardProps[] = this.props.args["cards"] || [];

    const cardStyle: React.CSSProperties = {
      border: '1px solid #ddd',
      borderRadius: '0',
      overflow: 'hidden',
      display: 'flex',
      flexDirection: 'column',
      height: 'auto',
    };

    const imageStyle: React.CSSProperties = {
      width: '100%',
      height: 'auto',
      objectFit: 'cover',
    };

    const contentStyle: React.CSSProperties = {
      padding: '10px',
      flex: 1,
      display: 'flex',
      flexDirection: 'column',
      justifyContent: 'center',
      alignItems: 'center',
    };

    const titleStyle: React.CSSProperties = {
      margin: '0 0 5px 0',
      fontSize: '16px',
      fontWeight: 'bold',
      textAlign: 'center',
    };

    const descriptionStyle: React.CSSProperties = {
      margin: '0',
      fontSize: '14px',
      color: '#666',
    };

    return (
      <div className="carousel-container">
        <button className="carousel-arrow carousel-arrow-left" onClick={this.handlePrev}>&lt;</button>
        <div className="carousel-content">
          {cards.slice(this.state.currentIndex, this.state.currentIndex + 3).map((card, index) =>
            this.renderCard(card, index, cardStyle, imageStyle, contentStyle, titleStyle, descriptionStyle)
          )}
        </div>
        <button className="carousel-arrow carousel-arrow-right" onClick={this.handleNext}>&gt;</button>
      </div>
    );
  }

  private handleNext = (): void => {
    const cards: CardProps[] = this.props.args["cards"] || [];
    this.setState(prevState => ({
      currentIndex: (prevState.currentIndex + 1) % (cards.length - 2)
    }));
  }

  private handlePrev = (): void => {
    const cards: CardProps[] = this.props.args["cards"] || [];
    this.setState(prevState => ({
      currentIndex: (prevState.currentIndex - 1 + (cards.length - 2)) % (cards.length - 2)
    }));
  }
}

class DefaultCarouselComponent extends BaseCarouselComponent {
  protected renderCard(card: CardProps, index: number, cardStyle: React.CSSProperties, imageStyle: React.CSSProperties, contentStyle: React.CSSProperties, titleStyle: React.CSSProperties): React.ReactNode {
    return (
      <div key={index} className="carousel-card" style={cardStyle} onClick={() => this.handleCardClick(card)}>
        <img src={card.image} alt={card.title} style={imageStyle} />
        <div style={contentStyle}>
          <h3 style={titleStyle}>{card.title}</h3>
        </div>
      </div>
    );
  }

  protected handleCardClick(card: CardProps): void {
    this.setState({ selectedTitle: card.title, selectedLink: card.link || null }, () => {
      Streamlit.setComponentValue({ title: card.title, link: card.link });
    });
  }
}

class AlternateCarouselComponent extends BaseCarouselComponent {
  protected renderCard(card: CardProps, index: number, cardStyle: React.CSSProperties, imageStyle: React.CSSProperties, contentStyle: React.CSSProperties, titleStyle: React.CSSProperties, descriptionStyle: React.CSSProperties): React.ReactNode {
    const alternateCardStyle = { ...cardStyle, flexDirection: 'row' as const, height: '150px' };
    const alternateImageStyle = { ...imageStyle, width: '30%', height: '100%' };
    const alternateContentStyle = { ...contentStyle, alignItems: 'flex-start' };
    const alternateTitleStyle = { ...titleStyle, textAlign: 'left' as const };

    return (
      <div key={index} className="carousel-card" style={alternateCardStyle} onClick={() => this.handleCardClick(card)}>
        <img src={card.image} alt={card.title} style={alternateImageStyle} />
        <div style={alternateContentStyle}>
          <h3 style={alternateTitleStyle}>{card.title}</h3>
          {card.description && <p style={descriptionStyle}>{card.description}</p>}
        </div>
      </div>
    );
  }

  protected handleCardClick(card: CardProps): void {
    this.setState({ selectedTitle: card.title, selectedDescription: card.description || null }, () => {
      Streamlit.setComponentValue({ title: card.title, description: card.description });
    });
  }
}

export const DefaultCarousel = withStreamlitConnection(DefaultCarouselComponent);
export const AlternateCarousel = withStreamlitConnection(AlternateCarouselComponent);
