import {
  Streamlit,
  StreamlitComponentBase,
  withStreamlitConnection,
} from "streamlit-component-lib"
import React, { ReactNode } from "react"

interface State {
  selectedImageTitle: string | null
  currentIndex: number
}

interface CardProps {
  image: string
  title: string
  description: string
}

class CarouselComponent extends StreamlitComponentBase<State> {
  public state: State = { selectedImageTitle: null, currentIndex: 0 }

  private cards: CardProps[] = [
    // ... your card data here ...
  ]

  public render = (): ReactNode => {
    const { theme } = this.props
    const style: React.CSSProperties = {
      width: '100%',
      fontFamily: theme?.font,
      color: theme?.textColor,
    }

    return (
      <div style={style}>
        <div id="carouselExampleControls" className="carousel slide" data-bs-ride="carousel">
          <div className="carousel-inner">
            {this.cards.map((card, index) => (
              <div className={`carousel-item ${index === this.state.currentIndex ? 'active' : ''}`} key={index}>
                <div className="card" onClick={() => this.handleCardClick(card.title)}>
                  <img src={card.image} className="card-img-top" alt={card.title} />
                  <div className="card-body">
                    <h5 className="card-title">{card.title}</h5>
                    <p className="card-text">{card.description}</p>
                  </div>
                </div>
              </div>
            ))}
          </div>
          <button className="carousel-control-prev" type="button" onClick={this.handlePrev}>
            <span className="carousel-control-prev-icon" aria-hidden="true"></span>
            <span className="visually-hidden">Previous</span>
          </button>
          <button className="carousel-control-next" type="button" onClick={this.handleNext}>
            <span className="carousel-control-next-icon" aria-hidden="true"></span>
            <span className="visually-hidden">Next</span>
          </button>
        </div>
        {this.state.selectedImageTitle && (
          <div className="mt-3">
            <p>Selected Image: {this.state.selectedImageTitle}</p>
          </div>
        )}
      </div>
    )
  }

  private handleCardClick = (title: string): void => {
    this.setState({ selectedImageTitle: title }, () => {
      Streamlit.setComponentValue(this.state.selectedImageTitle)
    })
  }

  private handleNext = (): void => {
    this.setState(prevState => ({
      currentIndex: (prevState.currentIndex + 1) % this.cards.length
    }))
  }

  private handlePrev = (): void => {
    this.setState(prevState => ({
      currentIndex: (prevState.currentIndex - 1 + this.cards.length) % this.cards.length
    }))
  }
}

export default withStreamlitConnection(CarouselComponent)
