import React from 'react';
import { Streamlit, StreamlitComponentBase, withStreamlitConnection } from "streamlit-component-lib";

interface State {
  selectedTitle: string | null;
  selectedLink: string | null;
  currentIndex: number;
}

interface CardProps {
  image: string;
  title: string;
  link: string;
  description?: string;
}

class CarouselComponent extends StreamlitComponentBase<State> {
  public state: State = { selectedTitle: null, selectedLink: null, currentIndex: 0 };

  public render = (): React.ReactNode => {
    const { theme } = this.props;
    const cards: CardProps[] = this.props.args["cards"] || [];
    const layout = this.props.args["layout"] || 'default';

    const cardStyle: React.CSSProperties = {
      border: `1px solid ${theme?.borderColor || '#ddd'}`,
      borderRadius: theme?.borderRadius || '0',
      overflow: 'hidden',
      display: 'flex',
      flexDirection: layout === 'alternate' ? 'row' : 'column',
      height: layout === 'alternate' ? '150px' : 'auto',
      backgroundColor: theme?.secondaryBackgroundColor || '#fff',
    };

    const imageStyle: React.CSSProperties = {
      width: layout === 'alternate' ? '30%' : '100%',
      height: layout === 'alternate' ? '100%' : 'auto',
      objectFit: 'cover',
    };

    const contentStyle: React.CSSProperties = {
      padding: '10px',
      flex: 1,
      display: 'flex',
      flexDirection: 'column',
      justifyContent: 'center',
      alignItems: layout === 'default' ? 'center' : 'flex-start',
    };

    const titleStyle: React.CSSProperties = {
      margin: '0 0 5px 0',
      fontSize: '16px',
      fontWeight: 'bold',
      textAlign: layout === 'default' ? 'center' : 'left',
      color: theme?.textColor || '#000',
      fontFamily: theme?.font || 'sans-serif',
    };

    const descriptionStyle: React.CSSProperties = {
      margin: '0',
      fontSize: '14px',
      color: theme?.secondaryTextColor || '#666',
      fontFamily: theme?.font || 'sans-serif',
    };

    const arrowStyle: React.CSSProperties = {
      backgroundColor: theme?.primaryColor || '#000',
      color: theme?.textColor || '#fff',
      border: 'none',
      borderRadius: '50%',
      width: '30px',
      height: '30px',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      cursor: 'pointer',
      fontSize: '18px',
    };

    return (
      <div style={{ fontFamily: theme?.font || 'sans-serif' }}>
        <button style={arrowStyle} onClick={this.handlePrev}>&lt;</button>
        <div style={{ display: 'flex', overflow: 'hidden' }}>
          {cards.slice(this.state.currentIndex, this.state.currentIndex + 3).map((card, index) => (
            <div key={index} style={cardStyle} onClick={() => this.handleCardClick(card.title, card.link, layout)}>
              <img src={card.image} alt={card.title} style={imageStyle} />
              <div style={contentStyle}>
                <h3 style={titleStyle}>{card.title}</h3>
                {layout === 'alternate' && card.description && (
                  <p style={descriptionStyle}>{card.description}</p>
                )}
              </div>
            </div>
          ))}
        </div>
        <button style={arrowStyle} onClick={this.handleNext}>&gt;</button>
      </div>
    );
  }

  private handleCardClick = (title: string, link: string, layout: string): void => {
    this.setState({ selectedTitle: title, selectedLink: link }, () => {
      if (layout === 'alternate') {
        Streamlit.setComponentValue({ title: title });
      } else {
        Streamlit.setComponentValue({ title: title, link: link });
      }
    });
  }

  private handleNext = (): void => {
    const cards: CardProps[] = this.props.args["cards"] || [];
    this.setState(prevState => ({
      currentIndex: (prevState.currentIndex + 1) % (cards.length - 2)
    }));
  }

  private handlePrev = (): void => {
    const cards: CardProps[] = this.props.args["cards"] || [];
    this.setState(prevState => ({
      currentIndex: (prevState.currentIndex - 1 + (cards.length - 2)) % (cards.length - 2)
    }));
  }
}

export default withStreamlitConnection(CarouselComponent);
